mapper.update:

update student
set violation_count = #{violationCount},
    violation_score = #{violationScore}
where id = #{id};

mapper.delete:
-- 根据ids批量删除员工
delete from student where id in

    #{id}

mapper.select:
-- 学员列表分页查询 select s.*,c.name as clazz_name from student s left join clazz c on s.clazz_id = c.id
and s.name like concat('%',#{name},'%')
and s.degree = #{degree}
and s.clazz_id = #{clazzId}
order by update_time desc
